mapper.delete:
DELETE article2
WHERE no=#{no}
AND password=#{password}

mapper.update:
update article2
set	name =#{name}
    ,title =#{title}
    ,content=#{content}
where no=#{no} and password=#{password}

mapper.select:

select no, title, name, regdate, readcount
from(
    select /*+INDEX_DESC(article2 pk_article2) */
    rownum rn, no, title, name, regdate, readcount
    from article2
    where rownum <= #{pageNum} * #{amount}
)
where rn > (#{pageNum} -1) * #{amount}
